def lookup(hand, pos, act, n):
    if n == 2:
        if pos == "n":
            if act == 1:
                return(np(hand))
            elif act == 2:
                return (nc(hand))
        elif pos == "BU":
            if act == 1:
                return (buopen(hand))
            elif act == 2:
                return buheadsuptenkey[int(buheadsupten[hand])] # BU heads up 10 bb
            elif act == 3:
                return buheadsupsevenkey[int(buheadsupseven[hand])] # BU heads up 7 bb
        elif pos == "BB":
            if act == 1:
                return bbvsbulimpkey[int(bbvsbulimp[hand])] # BB vs BU limp
            elif act == 2:
                return bbvsbuminraisekey[int(bbvsbuminraise[hand])] # BB vs BU min raise
            elif act == 3:
                return bbheadsupsevenkey[int(bbheadsupseven[hand])] # BB vs BU shove 7-9
    elif n == 3:
        if pos == "BB":
            if act == 1:
                return bbvsbuminraisekey[int(bbvsbuminraise[hand])] # BB vs BU min raise
            elif act == 2:
                return bbvssbraisekey[int(bbvssbraise[hand])] # BB vs SB 2.5
            elif act == 3:
                return bbvsbulimpkey[int(bbvsbulimp[hand])] # BB vs BU limp
            elif act == 4:
                return bbvssblimpkey[int(bbvssblimp[hand])] # BB vs SB limp
        elif pos == "SB":
            if act == 1:
                return sbvsbuminkey[int(sbvsbumin[hand])] # SB vs BU minraise
            if act == 2:
                return sbopen(hand) # SB open 3 handed
        elif pos == "BU":
            return buopen(hand) # BU open 3 handed

def sbopen(hand):
    for i in handlookupSB:
        if hand in handlookupSB[i]:
            return(i)
    return "fold"

def buopen(hand):
    for i in handlookupBU:
        if hand in handlookupBU[i]:
            return(i)
    return "fold"


def np(hand):
    return(nashpush[hand])

def nc(hand):
    return(nashcall[hand])
